import { Avatar, Button, Container, Grid, Paper } from '@material-ui/core';
import React, { useState } from 'react'
import useStyles from "./styles";
import { LockClockOutlined } from '@mui/icons-material';
import { Typography } from '@mui/material';
import Input from './Input';
import { useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';

const Auth = () => {
    const classes = useStyles();
    const [isSignUp,setSignUp] = useState(true);
    const [showPassword,setShowPassword] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = () =>{

    }

    const handleChange = () =>{

    }

    const handleShowPassword = () =>{
        setShowPassword((prevShowPasswpord) => !prevShowPasswpord);
    }

    const SwitchMode = () =>{
        setSignUp((prevSignUp) => !prevSignUp);
        handleShowPassword(false);
    }

  return (
    <Container component="main" maxWidth="xs" className={classes.root}>
        <Paper className={classes.Paper} elevation={3}>
            <Avatar className={classes.Avatar}>
                <LockClockOutlined color='secondary' ></LockClockOutlined>
            </Avatar>
            <Typography variant='h5'>{isSignUp?'Sign up':'Sign in'}</Typography>
            <form className={classes.form} onSubmit={handleSubmit}>
                <Grid Container spacing={1} >
                    {isSignUp && (
                        <>
                            <Input name='firstname' label="First Name" autoFocus half handleChange={handleChange} ></Input>
                            <Input name='lastname' label="last Name" half handleChange={handleChange} ></Input>
                        </>
                    )}
                    <Input name='email' label="Email" type="email" handleChange={handleChange} ></Input>
                    <Input name='password' label="Password" type={showPassword?'text':'password'} handleChange={handleChange} handleShowpassword={handleShowPassword} ></Input> 
                    {isSignUp && <Input name='confirmpassword' label="Repeat Password" type='password' handleChange={handleChange} ></Input>  }
                </Grid>
                <Button type='submit' fullWidth variant='contained' color="primary" className={classes.submit}>
                    {isSignUp?'Sign up':'Sign in'}
                </Button>

                <Grid container >
                        <Grid item>
                            <Button onClick={SwitchMode} className={classes.submit} >
                                {isSignUp?'Already have an account? sign in':"Don't have an account? sign up"}
                            </Button>
                        </Grid>
                </Grid>
            </form>
        </Paper>
    </Container>
  )
}

export default Auth;
