import { Divider, Typography,Button } from '@material-ui/core';
import { CardElement, Elements, ElementsConsumer } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import React from 'react';
import Review from './Review';

const stripePromise = loadStripe(process.env.REACT_APP_STRIPE_PUBLIC_KEY);

const PaymentForm = ({shippingDat,checkoutToken,backStep}) => {
  const handleSubmit = (event,elements,stripe)=>{
    event.preventDefault();

    if(!stripe || !elements) return;

    const cardElement = elements.getElement(Card)
  }
  return (
    <>
        <Review checkoutToken={checkoutToken} />
        <Divider />
        <Typography variant='h6' gutterBottom style={{margin:"20px 0"}}>Payment method</Typography>
        <Elements stripe={stripePromise}>
          <ElementsConsumer>
            {({elements,stripe})=>(
              <form onSubmit={(e)=>handleSubmit(e,elements,stripe)}>
                <CardElement  />
                <br/> <br/>
                <div style={{display:'flex',justifyContent:'space-between'}}>
                  <Button variant='outlined' onClick={backStep} >Back</Button>
                  <Button type="submit" variant='contained' disabled={!stripe} color="primary">pay {checkoutToken.live.subtotal.formatted_with_symbol}</Button>
                </div>
              </form>
            )}
          </ElementsConsumer>
        </Elements>
    </>
    );
};

export default PaymentForm;
